import React from "react";
import "../css/sidebar.scss";

const data = {
    nodes: [
        {
            id: "abc_172.22.22.214",
            name: "abc",
            nodes: [
                {
                    id: "abc_172.22.22.214.if.1",
                    name: "0",
                },
                {
                    id: "abc_172.22.22.214.if.3",
                    name: "0",
                },
                {
                    id: "abc_172.22.22.214.if.2",
                    name: "0",
                },
            ],
        },
        {
            id: "MON_LOGS_192.168.1.53",
            name: "MON_LOGS",
            nodes: [
                {
                    id: "MON_LOGS_192.168.1.53.if.1",
                    name: "lo",
                },
                {
                    id: "MON_LOGS_192.168.1.53.if.2",
                    name: "eth0",
                },
            ],
        },
    ],
};

class SidebarReports extends React.Component {
    state = {
        data: data,
        displayChild: null,
    };

    render() {
        return (
            <nav id='left-sidebar'>
                <div className='left-sidebar__functions'></div>
                <div className='left-sidebar__menu'>
                    <ul>
                        {this.state.data.nodes.map((element) => (
                            <li
                                key={element.id}
                                onClick={() => this.expandParent(element.id)}>
                                {element.name}
                                {element.id === this.state.displayChild &&
                                    element.nodes &&
                                    this._renderChildren(element.nodes)}
                            </li>
                        ))}
                    </ul>
                </div>
            </nav>
        );
    }
}

export default SidebarReports;
