import React from "react";
import "../css/sidebar.scss";

const data = {
    nodes: [
        {
            id: "1",
            name: "Финансовый департамент",
            nodes: [
                {
                    id: "2",
                    name: "Оперативный отчет о результатах деятельности ВТБ РБ",
                    nodes: [
                        {
                            id: "3",
                            name: "Оперативные показатели",
                        },
                    ],
                },
            ],
        },
    ],
};

class SidebarReports extends React.Component {
    state = {
        data: data,
        displayChild: null,
    };

    render() {
        return (
            <nav id='left-sidebar'>
                <div className='left-sidebar__functions'></div>
                <div className='left-sidebar__menu'>
                    <ul>
                        {this.state.data.nodes.map((element) => (
                            <li
                                key={element.id}
                                onClick={() => this.expandParent(element.id)}>
                                {element.name}
                                {element.id === this.state.displayChild &&
                                    element.nodes &&
                                    this._renderChildren(element.nodes)}
                            </li>
                        ))}
                    </ul>
                </div>
            </nav>
        );
    }

    _renderChildren = (nodes) => {
        const result = nodes.map((el) => {
            return <li key={el.id}> {el.name} </li>;
        });

        return <ul>{result}</ul>;
    };

    expandParent = (id) => {
        this.setState({
            displayChild: id,
        });
    };
}

export default SidebarReports;
